import logo from "./logo.svg";
import "./App.css";
import axios from "axios";
// import { w3cwebsocket as WebSocket } from "websocket";
import React, { useEffect, useState } from "react";

let url = "http://localhost:8000";

const File = ({ backendData }) => {
  // useEffect(() => {
  //   axios.get(`${url}/`).then((response) => {
  //     console.log(response.data);
  //   });
  // }, []);

  const sendDownloadRequest = async (id, name) => {
    console.log("sent download");
    axios.get(`${url}/`).then((response) => {
      console.log(response.data);
    });
    // try {
    //   const t = await fetch(`${url}/`, {
    //     method: "GET",
    //     headers: { "Content-Type": "application/json" },
    //   });
    //   console.log("t", t);
    // } catch (e) {
    //   console.error(e);
    // }
  };

  return (
    <div className="file">
      {/* <h2>Id: {backendData[0]}</h2> */}
      <div>
        {/* <h3>Name: {backendData[1][0]}</h3> */}
        {/* <h3>Users</h3> */}
        {/* {backendData[1][1].map((u) => { */}
        {/* return <div>{u.displayName}</div>; */}
        {/* })} */}
        <div className="download">
          <button onClick={sendDownloadRequest}>Download</button>
        </div>
      </div>
    </div>
  );
};

function App() {
  const [files, setFiles] = useState({});
  const events = new EventSource(`${url}/events`);

  useEffect(() => {
    axios.get(`${url}/files`).then((data) => {
      setFiles(data.data);
      console.log("FILES", data.data);
    });
  }, []);

  // useEffect(() => {
  //   axios.get(`${url}/`).then((response) => {
  //     console.log(response.data);
  //   });
  // }, []);

  useEffect(() => {
    events.onmessage = (event) => {
      const parsedData = JSON.parse(event.data);
      console.log("PARSED", parsedData);
      if (parsedData.length === 1) {
        const remove = { ...files };
        delete remove[parsedData[0]];
        console.log("REMOVED", remove);
        setFiles(remove);
      } else {
        const key = parsedData[0];
        setFiles({ ...files, [key]: [parsedData[1], parsedData[2]] });
      }
    };
    return () => {
      events.close();
    };
  }, []);

  function sendDownloadRequest(id, name) {
    console.log("sent download");
    // axios
    //   .post(`${url}/download`, {
    //     id: id,
    //     name: name,
    //   })
    //   .then((res) => console.log(res));
    const res = axios.get(`${url}/`);
    console.log("here", res);
  }

  return (
    <div className="App">
      {Object.entries(files).map(([id, perm]) => {
        return <File backendData={[]} />;
      })}
    </div>
  );
}

export default App;
