import logo from "./logo.svg";
import "./App.css";
import axios from "axios";
// import { w3cwebsocket as WebSocket } from "websocket";
import React, { useEffect, useState } from "react";

let url = "http://localhost:8000";

const File = ({ backendData }) => {
  // useEffect(() => {
  //   axios.get(`${url}/`).then((response) => {
  //     console.log(response.data);
  //   });
  // }, []);

  // const sendDownloadRequest = (id, name) => {
  //   console.log("sent download");
  //   // axios.get(`${url}/`).then((response) => {
  //   //   console.log(response.data);
  //   // });
  //   try {
  //     const t = fetch(`${url}/`, {
  //       method: "GET",
  //       headers: { "Content-Type": "application/json" },
  //     });
  //     console.log("t", t);
  //   } catch (e) {
  //     console.error(e);
  //   }
  // };
  console.log(backendData[0]);
  return (
    <div className="file">
      <div>
        <h3>Name: {backendData[1][0]}</h3>
        <h3>Users</h3>
        {backendData[1][1].map((u) => {
          return <div>{u.displayName}</div>;
        })}
        <div className="download">
          <button
            onClick={() => {
              axios
                .post(`${url}/download`, {
                  id: backendData[0],
                  name: backendData[1][0],
                })
                .then((res) => console.log(res));
              console.log("clicked");
            }}
          >
            Download
          </button>
        </div>
      </div>
    </div>
  );
};

function App() {
  const [files, setFiles] = useState({});

  useEffect(() => {
    axios.get(`${url}/files`).then((data) => {
      setFiles(data.data);
      console.log("FILES", data.data);
    });
  }, []);

  // useEffect(() => {
  //   axios.get(`${url}/`).then((response) => {
  //     console.log(response.data);
  //   });
  // }, []);

  useEffect(() => {
    const events = new EventSource(`${url}/events`);
    events.onmessage = (event) => {
      const parsedData = JSON.parse(event.data);
      console.log("PARSED", parsedData);
      if (parsedData.length === 1) {
        const remove = { ...files };
        delete remove[parsedData[0]];
        console.log("REMOVED", remove);
        setFiles(remove);
      } else {
        const key = parsedData[0];
        setFiles({ ...files, [key]: [parsedData[1], parsedData[2]] });
      }
    };
    return () => {
      events.close();
    };
  }, []);

  // function sendDownloadRequest(id, name) {
  //   console.log("sent download");
  //   axios
  //     .post(`${url}/download`, {
  //       id: id,
  //       name: name,
  //     })
  //     .then((res) => console.log(res));
  //   console.log("here", res);
  // }

  return (
    <div className="App">
      {Object.entries(files).map(([id, perm]) => {
        return <File backendData={[id, perm]} />;
      })}
    </div>
  );
}

export default App;
